import { rmSync } from 'fs';

import { resolve } from './util.js';

console.log('[lume] Running postbuild script...');

try {
  // Remove empty font/main JS files generated by Webpack
  rmSync(resolve('dist/styles/font.js'), { force: true });
  rmSync(resolve('dist/styles/main.js'), { force: true });

  // Remove font.scss (shouldn't be used in production)
  rmSync(resolve('dist/scss/font.scss'), { force: true });
} catch (error) {
  console.error('[lume]', error);
}

console.log('[lume] Postbuild script successful!');
